// Get AI agent output (assuming it's in the field 'output')
const data = $json.output;

// Prepare final result object with EMPTY values
let result = {
  candidatename: "",
  email: "",
  phone: "",
  currentdesignation: "",
  totalexperience: "",
  issuitableforrole: "",
  score_out_of_100: "",
  summary_of_suitability: ""
};

// Split lines and extract matching fields
data.split('\n').forEach(line => {
  const [key, ...valueParts] = line.split(':');
  if (!key || !valueParts) return;

  const rawKey = key.trim().toLowerCase();
  const value = valueParts.join(':').trim();

  if (rawKey.includes("name")) {
    result.candidatename = value;
  }
  else if (rawKey.includes("email")) {
    result.email = value;
  }
  else if (rawKey.includes("phone") || rawKey.includes("mobile")) {
    result.phone = value;
  }
  else if (rawKey.includes("designation") || rawKey.includes("current role")) {
    result.currentdesignation = value;
  }
  else if (rawKey.includes("experience")) {
    result.totalexperience = value;
  }
  else if (rawKey.includes("suitable")) {
    result.issuitableforrole = value;
  }
  else if (rawKey.includes("score")) {
    result.score_out_of_100 = value;
  }
  else if (
    rawKey.includes("summary") ||
    rawKey.includes("basis") ||
    rawKey.includes("reason") ||
    rawKey.includes("explanation")
  ) {
    result.summary_of_suitability = value;
  }
});

// Return final structured JSON
return [
  {
    json: result
  }
];

const item = items[0];
const binaries = item.binary || {};

const fileKeys = Object.keys(binaries);

if (fileKeys.length === 0) {
  return [
    {
      json: {
        success: false,
        message: "No files found in binary data.",
        receivedKeys: Object.keys(item)   // debugging
      }
    }
  ];
}

return fileKeys.map(key => {
  return {
    json: {
      success: true,
      fileName: binaries[key].fileName,
      mimeType: binaries[key].mimeType,
      fieldName: key
    },
    binary: {
      data: binaries[key]
    }
  };
});

// Normalize PDF/Text/Doc responses to ONE SAME key: "input"

return items.map(item => {

    let data = null;

    // PDF output key = "text"
    if (item.json.text) {
        data = item.json.text;
    }

    // Word doc output key = "output"
    if (item.json.output) {
        data = item.json.output;
    }

    // Final unified JSON
    item.json = {
        input: data,                        // <--- unified key name
        merged_at: new Date().toISOString() // optional
    };

    return item;
});

